import PropTypes from "prop-types";
import { Card } from "react-bootstrap";
import Rating from "../Rating/Rating";
import Button from "react-bootstrap";

const Book = ({ title, author, genre, publicationYear, coverImageURL, rating , onButtonClick}) => {

  return (
    <Card style={{ width: "20rem", margin: "15px", padding: "15px", textAlign: "center" }}>
      <Card.Img variant="top" src={coverImageURL} alt={title} style={{ height: "300px", objectFit: "cover" }} />
      <Card.Body>
        <Card.Title><strong>  {title} </strong></Card.Title>
        <Card.Text><strong>Author : </strong> {author}</Card.Text>
        <Card.Text><strong>Gender : </strong> {genre}</Card.Text>
        <Card.Text><strong>Published in : </strong> {publicationYear}</Card.Text>
        <Rating rating={rating} />
        <Button onClick={onButtonClick} style={{ marginTop: '10px', padding: '8px 16px', cursor: 'pointer' }}>
        Read More
      </Button>


      </Card.Body>
    </Card>
  );
};

Book.propTypes = {
  title: PropTypes.string,
  author: PropTypes.string,
  genre: PropTypes.string,
  publicationYear: PropTypes.number,
  coverImageURL: PropTypes.string,
  rating: PropTypes.number,
};

Book.defaultProps = {
  title: "Unknown Title",
  author: "Unknown Author",
  genre: "Unknown Gender",
  publicationYear: 0,
  coverImageURL: "https://via.placeholder.com/150",
  rating: 3,
};

export default Book;
